import { Route, Routes, useNavigate } from "react-router-dom";
import actionPromise from "./ActionPromise";
import store from "./Store";
import { useState } from "react";
import AdminEditItem from "./AdminEditItem";


function CategoryInfo({category,list}){
    const [name, setName] = useState(category.name);
    const navigate = useNavigate();

    let gql = (url, query, variables) =>
      fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + localStorage.authToken,
          Accept: "application/json",
        },
        body: JSON.stringify({ query, variables }),
      }).then((res) => console.log(res.json()));

    function handleSubmit(event) {
      event.preventDefault();
      store.dispatch(
        actionPromise(
          "changeCategoryDetails",
          gql(
            "http://shop-roles.node.ed.asmer.org.ua/graphql",
            `mutation changeDetails($categor:CategoryInput){
              CategoryUpsert(category:$categor){
              name, goods {name}
              }
            }`,
            {
              categor: {
                _id: category._id,
                name: name
              },
            }
          )
        )
      );
    }
     function deleteCategory(event) {
       event.preventDefault();
       store.dispatch(
         actionPromise(
           "deleteCategory",
           gql(
             "http://shop-roles.node.ed.asmer.org.ua/graphql",
             `mutation delete($category:CategoryInput){
              CategoryDelete(category:$category){
              name
              }
            }`,
             {
               category: {
                 _id: category._id,
                 name: name,
               },
             }
           )
         )
       );
       navigate(-1);
     }

    return (
      <div className="category__edit">
        <div>
          <h1 className="main__form__title">Category</h1>
          <form onSubmit={handleSubmit} className="main__form">
            <div className="main__form__input">
              <label>Name</label>
              <input
                value={name}
                onChange={(event) => setName(event.target.value)}
                type="text"
              />
            </div>
            <input
              disabled={name.length >= 1 ? false : true}
              type="submit"
              value="Save changes"
              className="main__form__submit"
            />
            <button
              className="main__form__delete main__form__submit"
              onClick={(e) => deleteCategory(e)}
            >
              Delete category
            </button>
          </form>
        </div>
        <div>
          {category.goods && (
            <>
              <h1 className="main__form__title">Goods</h1>
              {category.goods.map((i) => (
                <AdminEditItem
                  categories={list}
                  item={i}
                  key={"good" + Math.random()}
                />
              ))}
            </>
          )}
        </div>
        <form onSubmit={handleSubmit} className="main__form">
          <div className="main__form__input">
            <label>Name</label>
            <input
              value={name}
              onChange={(event) => setName(event.target.value)}
              type="text"
            />
          </div>
          <div className="main__form__input main__form__description">
            <label>Description</label>
            <textarea
              value={description}
              onChange={(event) => setDescription(event.target.value)}
              type="text"
            />
          </div>
          <div className="main__form__input">
            <label>Price</label>
            <input
              value={price}
              onChange={(event) => setPrice(event.target.value)}
              type="text"
            />
          </div>
          <legend>Categories</legend>
          {categories &&
            categories.data.CategoryFind.map((category) => {
              return (
                <div key={"checkbox" + Math.random()}>
                  <input
                    type="checkbox"
                    name="category"
                    value={category.name}
                    defaultChecked={checkSelectedCategories(
                      selectedCategories,
                      category.name
                    )}
                    onChange={(e) => {
                      if (
                        !checkSelectedCategories(
                          selectedCategories,
                          category.name
                        )
                      ) {
                        selectedCategories.push(category);
                        console.log(selectedCategories);
                        e.target.checked = true;
                      } else {
                        {
                          selectedCategories = selectedCategories.filter(
                            (i) => {
                              return i.name !== category.name;
                            }
                          );
                          console.log(selectedCategories);
                          e.target.checked = false;
                        }
                      }
                    }}
                  />
                  <label>{category.name}</label>
                </div>
              );
            })}
          <input
            disabled={name.length >= 1 ? false : true}
            type="submit"
            value="Save changes"
            className="main__form__submit"
          />
          <button
            className="main__form__delete main__form__submit"
            onClick={(e) => deleteItem(e)}
          >
            Delete item
          </button>
        </form>
      </div>
    );
}
function AdminCategoryInfo({ categories }) {
  return (
    <Routes>
      {categories ?
        categories.data.CategoryFind.map((category) => {
          return (
            <Route
              path={category._id}
              element={<CategoryInfo category={category} list={categories} />}
              key={"route" + category._id}
            />
          );
        }):<>hi</>}
    </Routes>
  );
}
export default AdminCategoryInfo;
