 import "./Admin.css";
 import {Link, useLocation} from "react-router-dom";
 function AdminCategoriesMenu({ categories }) {
   const location = useLocation();
   console.log(categories);
   return (
    <>
     <div className="categories--list">
       {categories &&
         categories.data.CategoryFind.map((category) => {
           return (
             <Link
               className="categories--list__item"
               key={"cat"+category._id}
               to={location.pathname + "/" + category._id}
             >
               {category.name}
             </Link>
           );
         })}
     </div>
          <form onSubmit={handleSubmit} className="main__form">
        <div className="main__form__input">
          <label>Name</label>
          <input
            value={name}
            onChange={(event) => setName(event.target.value)}
            type="text"
          />
        </div>
        <div className="main__form__input main__form__description">
          <label>Description</label>
          <textarea
            value={description}
            onChange={(event) => setDescription(event.target.value)}
            type="text"
          />
        </div>
        <div className="main__form__input">
          <label>Price</label>
          <input
            value={price}
            onChange={(event) => setPrice(event.target.value)}
            type="text"
          />
        </div>
        <legend>Categories</legend>
        {categories &&
          categories.data.CategoryFind.map((category) => {
            return (
              <div key={"checkbox" + Math.random()}>
                <input
                  type="checkbox"
                  name="category"
                  value={category.name}
                  defaultChecked={checkSelectedCategories(
                    selectedCategories,
                    category.name
                  )}
                  onChange={(e) => {
                    if (
                      !checkSelectedCategories(
                        selectedCategories,
                        category.name
                      )
                    ) {
                      selectedCategories.push(category);
                      console.log(selectedCategories);
                      e.target.checked = true;
                    } else {
                      {
                        selectedCategories = selectedCategories.filter((i) => {
                          return i.name !== category.name;
                        });
                        console.log(selectedCategories);
                        e.target.checked = false;
                      }
                    }
                  }}
                />
                <label>{category.name}</label>
              </div>
            );
          })}
        <input
          disabled={name.length >= 1 ? false : true}
          type="submit"
          value="Save changes"
          className="main__form__submit"
        />
        <button
          className="main__form__delete main__form__submit"
          onClick={(e) => deleteItem(e)}
        >
          Delete item
        </button>
      </form>
      </>
   );
 }
export default AdminCategoriesMenu;
