import actionPromise from "./ActionPromise";
import store from "./Store";
import {useState,useEffect} from "react";
function ProfileInfo() {
  const [login, setLogin] = useState("");
  const [nick, setNick] = useState("");
  const [password, setPassword] = useState("");
  let gql = (url, query, variables) =>
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        ...(localStorage.authToken
          ? { Authorization: "Bearer " + localStorage.authToken }
          : {}),
        Accept: "application/json",
      },
      body: JSON.stringify({ query, variables }),
    }).then((res) => console.log(res.json()));

  function handleSubmit(event) {
    event.preventDefault();
    console.log("success")
  store.dispatch(
    actionPromise(
      "signUp",
      gql(
        "http://shop-roles.node.ed.asmer.org.ua/graphql",
        `mutation signup($user:UserInput){
              UserUpsert(user:$user){
              login,  _id
              }
            }`,
        { user: { login: login, nick:"mama"} }
      )
    )
  );
  }
  function deleteUser(event){
    event.preventDefault();
  }
  function parseJwt(token) {
    if (!token) {
      return;
    }
    const base64Url = token.split(".")[1];
    const base64 = base64Url.replace("-", "+").replace("_", "/");
    return JSON.parse(window.atob(base64));
  }
  useEffect(() => setLogin(parseJwt(localStorage.authToken).sub.login), []);
  useEffect(() => setNick(parseJwt(localStorage.authToken).sub.nick?parseJwt(localStorage.authToken).sub.nick:""), []);
  return (
    <div>
      <form onSubmit={handleSubmit} className="main__form">
        <h1 className="main__form__title">Your cabinet</h1>
        <div className="main__form__input">
          <label>Login</label>
          <input
            value={login}
            onChange={(event) => setLogin(event.target.value)}
            type="text"
          />
        </div>
        <div className="main__form__input">
          <label>Nick</label>
          <input
            value={nick}
            onChange={(event) => setNick(event.target.value)}
          />
        </div>
        <div className="main__form__input">
          <label>New password</label>
          <input
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
          {password.length > 0 && password.length < 6 && (
            <span className="main__form__warning">
              password must contain then 6 symbols
            </span>
          )}
        </div>
        <input
          disabled={
            (login.length >= 1 && password.length === 0) ||
            (login.length >= 1 && password.length >= 6)
              ? false
              : true
          }
          type="submit"
          value="Save changes"
          className="main__form__submit"
        />
        <button
          className="main__form__submit"
          onClick={() => {
            localStorage.clear();
            window.location.reload();
            store.dispatch(
              actionPromise(
                "signUp",
                gql(
                  "http://shop-roles.node.ed.asmer.org.ua/graphql",
                  `mutation delete($user:UserInput){
              UserDelete(user:$user){
              login,  _id
              }
            }`,
                  { user: { login: login } }
                )
              )
            );

          }}
        >
          Delete account
        </button>
      </form>
      <p className="form__logout">
        Do you want to log out?
        <button
          className="main__form__submit"
          onClick={() => {
            localStorage.clear();
            window.location.reload();
          }}
        >
          Log out
        </button>
      </p>
    </div>
  );
}
export default ProfileInfo;
