import classes from "./Products.module.css";
import ProductCard from "./ProductCard";
function Products({ data }) {
  let products = []
  let maxAmount = 9;
  if(data){
  if (data.data.CategoryFindOne) {
      products = data.data.CategoryFindOne.goods
    }
    if (data.data.CategoryFind) {
      data.data.CategoryFind.forEach((category) => {
        if (category.goods) {
          products = category.goods.slice(maxAmount)
        }
      });
    }
  }
  return (
    <section>
      <div className={classes.products}>
        {data&&products.map((product) => {return <ProductCard data={product} key={product._id+Math.random()} />})}
      </div>
    </section>
  );
}
export default Products;
