import { useState } from "react";
import actionPromise from "./ActionPromise";
import store from "./Store";
function AddNewItem({ categories }) {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [imgUrl, setImgUrl] = useState("");
  let selectedCategories = [];
  function checkSelectedCategories(list, categoryName) {
    return list.filter((c) => c.name === categoryName)[0] ? true : false;
  }
  let gql = (url, query, variables) =>
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.authToken,
        Accept: "application/json",
      },
      body: JSON.stringify({ query, variables }),
    }).then((res) => console.log(res.json()));

  function handleSubmit(event) {
    event.preventDefault();
    store.dispatch(
      actionPromise(
        "addNewGood",
        gql(
          "http://shop-roles.node.ed.asmer.org.ua/graphql",
          `mutation addGood($good:GoodInput){
              GoodUpsert(good:$good){
              name, price, categories{name}
              }
            }`,
          {
            good: {
              name: name,
              description: description,
              price: Number(price),
              categories: selectedCategories.map((e) => {
                return { name: e.name, _id: e._id };
              }),
            },
          }
        )
      )
    );
    store.dispatch(
      actionPromise(
        "addImage",
        gql(
          "http://shop-roles.node.ed.asmer.org.ua/graphql",
          `mutation uploadImage($image:ImageInput){
              ImageUpsert(image:$image){
                _id,good{name},url
              }
            }`,
          {
            image: {
                imgUrl,
                good: {
                name: name,
                description: description,
                price: Number(price),
                categories: selectedCategories.map((e) => {
                  return { name: e.name, _id: e._id };
                }),
              },
            },
          }
        )
      )
    );
  }
  return (
    <div className="edit__good">
      <form
        onSubmit={handleSubmit}
        className="main__form"
        action="/upload"
        method="post"
        encType="multipart/form-data"
      >
        <div className="main__form__input">
          <label>Name</label>
          <input
            value={name}
            onChange={(event) => setName(event.target.value)}
            type="text"
          />
        </div>
        <div className="main__form__input main__form__description">
          <label>Description</label>
          <textarea
            value={description}
            onChange={(event) => setDescription(event.target.value)}
            type="text"
          />
        </div>
        <div className="main__form__input">
          <label>Price</label>
          <input
            value={price}
            onChange={(event) => setPrice(event.target.value)}
            type="text"
          />
        </div>
        <legend>Categories</legend>
        {categories &&
          categories.data.CategoryFind.map((category) => {
            return (
              <div key={"checkbox" + Math.random()}>
                <input
                  type="checkbox"
                  name="category"
                  value={category.name}
                  defaultChecked={false}
                  onChange={(e) => {
                    if (
                      !checkSelectedCategories(
                        selectedCategories,
                        category.name
                      )
                    ) {
                      selectedCategories.push(category);
                      console.log(selectedCategories);
                      e.target.checked = true;
                    } else {
                      {
                        selectedCategories = selectedCategories.filter((i) => {
                          return i.name !== category.name;
                        });
                        console.log(selectedCategories);
                        e.target.checked = false;
                      }
                    }
                  }}
                />
                <label>{category.name}</label>
              </div>
            );
          })}
          <input
            type="file"
            name="photo"
            onChange={(e) => {
                setImgUrl(e.target.files[0])}}
          />
        <input
          disabled={name.length >= 1 ? false : true}
          type="submit"
          value="Save changes"
          className="main__form__submit"
        />
      </form>
    </div>
  );
}
export default AddNewItem;
