              }
              type="submit"
              value="Save changes"
              className="main__form__submit"
            />
          </form>
          <p className="form__delete">
            Do you want to delete account?
            <button
              className="main__form__submit"
              onClick={() => {
                store.dispatch(
                  actionPromise(
                    "delete",
                    gql(
                      "http://shop-roles.node.ed.asmer.org.ua/graphql",
                      `mutation delete($user:UserInput){
                    UserDelete(user:$user){login}
                  }`,
                      {
                        user: {
                          login: login,
                        },
                      }
                    )
                  )
                );
              }}
            >
              Delete
            </button>
          </p>
        </div>
      </>
    );
}
function AdminCategoryInfo({ categories }) {
  return (
    <Routes>
      {categories &&
        categories.data.CategoryFind.map((category) => {
          return (
            <Route
              path={category._id}
