import classes from "./Products.module.css";
import ProductCard from "./ProductCard";
import { createRef, useRef } from "react";
import { useEffect,useState } from "react";
function Products({ data }) {
  let products = []
  let maxIndex = 9;
  const [allProducts,setAllProducts]=useState([])
  if(data){
  if (data.data.CategoryFindOne) {
      products = data.data.CategoryFindOne.goods
    }
    if (data.data.CategoryFind) {
      data.data.CategoryFind.forEach((category) => {
        if (category.goods) {
          category.goods.forEach((product) => {
            products.push(product);
          });
        }
      });
    }
   useEffect(() => setAllProducts(products.slice(0, maxIndex)), []);
  }
  const lastItem = createRef();
  const observerLoader = useRef();
  const actionInSight = (entries) => {
    if (entries[0].isIntersecting) {
      maxIndex = maxIndex+4;
      useEffect(()=>setAllProducts(products.slice(0, maxIndex)),[]);
    }
  };
  useEffect(() => {
    if (observerLoader.current) {
      observerLoader.current.disconnect();
    }

    observerLoader.current = new IntersectionObserver(actionInSight);
    if (lastItem.current) {
      observerLoader.current.observe(lastItem.current);
    }
  }, [lastItem]);
  return (
    <section>
      <div className={classes.products}>
        {data &&
          allProducts.map((product, index) => {
            if (index + 1 === products.length) {
              return (
                <ProductCard
                  data={product}
                  key={product._id + Math.random()}
                  ref={lastItem}
                />
              );
            }
            return (
              <ProductCard data={product} key={product._id + Math.random()} />
            );
          })}
      </div>
    </section>
  );
}
export default Products;
