import classes from "./Products.module.css";
import { useState } from "react";
import actionPromise from "./ActionPromise";
import store from "./Store";
function AdminEditItem({ item }) {
    const [name, setName] = useState(item.name);
    const [description, setDescription] = useState(item.description);
    const [price, setPrice] = useState(item.price);
    let gql = (url, query, variables) =>
      fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
            Authorization: "Bearer " + localStorage.authToken ,
          Accept: "application/json",
        },
        body: JSON.stringify({ query, variables }),
      }).then((res) => console.log(res.json()));

    function handleSubmit(event) {
      event.preventDefault();
      store.dispatch(
        actionPromise(
          "changeDetails",
          gql(
            "http://shop-roles.node.ed.asmer.org.ua/graphql",
            `mutation changeDetails($good:GoodInput){
              GoodUpsert(good:$good){
              name, price
              }
            }`,
            {
              good: {
                _id: item._id,
                name: name,
                description: description,
                price: Number(price)
              },
            }
          )
        )
      );
    }
    function deleteItem(event) {
      event.preventDefault();
      store.dispatch(
        actionPromise(
          "changeDetails",
          gql(
            "http://shop-roles.node.ed.asmer.org.ua/graphql",
            `mutation changeDetails($good:GoodInput){
              GoodDelete(good:$good){
              name
              }
            }`,
            {
              good: {
                _id: item._id,
                name: name
              },
            }
          )
        )
      );
    }
  return (
    <div>
      <form onSubmit={handleSubmit} className="main__form">
        <div className="main__form__input">
          <label>Name</label>
          <input
            value={name}
            onChange={(event) => setName(event.target.value)}
            type="text"
          />
        </div>
        <div className="main__form__input">
          <label>Description</label>
          <input
            value={description}
            onChange={(event) => setDescription(event.target.value)}
            type="text"
          />
        </div>
        <div className="main__form__input">
          <label>Price</label>
          <input
            value={price}
            onChange={(event) => setPrice(event.target.value)}
            type="text"
          />
        </div>

        <input
          disabled={name.length >= 1 ? false : true}
          type="submit"
          value="Save changes"
          className="main__form__submit"
        />
      </form>
      <button onClick={(e)=>deleteItem(e)}>Delete item</button>
    </div>
  );
}
export default AdminEditItem;
